insert into movie (id, title, description, genre, IMAGEURL) values ('1', 'Star Wars - A new hope', 'Luke Skywalker joins forces with a Jedi Knight, a cocky pilot, a Wookiee, and two droids to save the galaxy from the Empires world-destroying battle-station, while also attempting to rescue Princess Leia from the evil Darth Vader.', ' Action, Adventure, Fantasy', 'https://upload.wikimedia.org/wikipedia/commons/a/ac/Star_Wars_-_A_New_Hope%2C_filming_in_Death_Valley.jpg');
insert into movie (id, title, description, genre, IMAGEURL) values ('2', 'Star Wars - The Empire Strikes Back', 'After the rebels are overpowered by the Empire on their newly established base, Luke Skywalker begins Jedi training with Master Yoda. His friends accept shelter from a questionable ally as Darth Vader hunts them in a plan to capture Luke.', ' Action, Adventure, Fantasy', 'https://upload.wikimedia.org/wikipedia/commons/d/dd/Mark_Hamill_1980.jpg');
insert into movie (id, title, description, genre, IMAGEURL) values ('3', 'Star Wars - Return of the Jedi', 'After a daring mission to rescue Han Solo from Jabba the Hutt, the rebels dispatch to Endor to destroy a more powerful Death Star. Meanwhile, Luke struggles to help Vader back from the dark side without falling into the Emperors trap.', ' Action, Adventure, Fantasy', 'https://upload.wikimedia.org/wikipedia/commons/6/65/Expedition_45_%27Return_of_the_Jedi%27_crew_poster.jpg');
